mapper.select:
select

from air_product p
where date_format(p.ar_from_date, '%Y-%m-%d') = date_format( #{fromDate}, '%Y-%m-%d')
    and p.ar_from = #{from} and p.ar_to = #{to}
    and p.ar_res_cnt > #{ea}
    and p.agop_code = #{airGrade}
order by p.ano
limit #{pagination.limitStart}, #{amount}

select

from air_product p
where date_format(p.ar_to_date, '%Y-%m-%d') = date_format( #{toDate}, '%Y-%m-%d')
and p.ar_from = #{to} and p.ar_to = #{from}
and p.ar_res_cnt > #{ea}
and p.agop_code = #{airGrade}
order by p.ano
limit #{pagination.limitStart}, #{amount}
