mapper.select:
select pno, rno, mno, pay_tot_price, pay_date, pay_state
from Pay
order by pno;

select pno, rno, mno, pay_tot_price, pay_date, pay_state, pay_method
from Pay
where pno = #{pno};

select pno
from Pay
where rno = #{rno};

mapper.update:
UPDATE Pay
SET pay_state = '결제 취소'
WHERE pno = #{pno};

mapper.select:
select count(*)
from refund
where pno = #{pno};

select rf_reason, rf_date, rf_state
from refund
where pno = #{pno};
